package Operators;

public class BitwiseOperator {

	public static void main(String[] args) 
	{
		int a=12;
		int b=10;
		System.out.println(a+"'s binary number is "+Integer.toBinaryString(a));
		System.out.println(b+"'s binary number is "+Integer.toBinaryString(b));
		System.out.println("a&b= "+(a&b));
		System.out.println("a|b= "+(a|b));
		System.out.println("a^b= "+(a^b));
		System.out.println("~a= "+(~a));
		
		// TODO Auto-generated method stub

	}

}
